import React, { useState, useEffect } from 'react';
import './MyStopwatch.css';

const MyStopwatch = ({ setStartFunction, setStopFunction }) => {
  const [time, setTime] = useState(0);
  const [intervalId, setIntervalId] = useState(null);

  useEffect(() => {
    setStartFunction(() => start);
    setStopFunction(() => stop);
  }, [setStartFunction, setStopFunction]);

  const start = () => {
    if (!intervalId) {
      const id = setInterval(() => setTime((prev) => prev + 1), 1000);
      setIntervalId(id);
    }
  };

  const stop = () => {
    clearInterval(intervalId);
    setIntervalId(null);
  };

  const formatTime = () => {
    const hours = Math.floor(time / 3600);
    const minutes = Math.floor((time % 3600) / 60);
    const seconds = time % 60;

    return (
      <>
        <span>{String(hours).padStart(2, '0')}:</span>
        <span>{String(minutes).padStart(2, '0')}:</span>
        <span>{String(seconds).padStart(2, '0')}</span>
      </>
    );
  };

  return <div className="timer-div">{formatTime()}</div>;
};

export default MyStopwatch;
