import React, { useState, useEffect } from 'react';
import Card from '../Card/Card';
import './Board.css';
import Modal from '../Modal/Modal';
import useModal from '../Modal/useModal';

const Board = props => {
  const [cards, setCards] = useState(props.cards)
  const [checkers, setCheckers] = useState([])
  const [completed, setCompleted] = useState([])
  const [isTimerStarted, setIsTimerStarted] = useState(false);
  const { isShowing, toggle } = useModal();

  const onCardClick = card => () => {
    if (!isTimerStarted) {
      setIsTimerStarted(true); // Start the timer on the first click
      props.startTimer(); // Call the timer's start function
    }

    if (checkersFull(checkers) || cardAlreadyInCheckers(checkers, card))
      return
    const newCheckers = [...checkers, card]
    setCheckers(newCheckers)
    const cardsInCheckersMatched = validateCheckers(newCheckers)
    console.log('checkersFull', checkersFull(newCheckers))

    if (cardsInCheckersMatched) {
      setCompleted([...completed, newCheckers[0].type])

    }

    if (checkersFull(newCheckers)) {
      resetCheckersAfter(1000)
    }


      if (completed.length === 12) {
        resetCompletedAfter(4000)
        toggle();
      }



    function resetCompletedAfter(time) {
      setTimeout(() => {
     setCompleted([])
   }, time)
    }



    function validateCheckers(checkers){
      return checkers.length === 2 &&
      checkers[0].type === checkers[1].type
    }

    function cardAlreadyInCheckers(checkers, card){
      return checkers.length === 1 && checkers[0].id === card.id
    }

    function checkersFull(checkers){
      return checkers.length === 2
    }

    function resetCheckersAfter(time) {
      setTimeout(() => {
        setCheckers([])
      }, time)
    }
  }



  useEffect(() => {
    const newCards = cards.map(card => ({
      ...card,
      flipped:
        checkers.find(c => c.id === card.id) ||
        completed.includes(card.type),
    }))
    setCards(newCards)
    // eslint-disable-next-line
  }, [checkers, completed])








  return (
    <>
    <div className="Board">
      {cards.map(card => (
        <Card {...card} onClick={onCardClick(card)} key={card.id} />
      ))}
      </div>

      <div className="App">
      <Modal
        isShowing={isShowing}
        hide={toggle}
      />
      </div>
      </>
  )
}

export default Board

// Build Cards Function
function buildCards() {
  let id = 0;
  const images = {
    Aang, Appa, Katara, PrinceZuko,
    Sokka, Toph, Iroh_smiling, Mai, momo, Tylee, Azulaa, Bumi,
  };
  const cards = Object.keys(images).reduce((result, item) => {
    const getCard = () => ({
      id: id++,
      type: item,
      backImg,
      frontImg: images[item],
      flipped: false,
    });
    return [...result, getCard(), getCard()];
  }, []);
  return shuffle(cards);
}

// Shuffle Function
function shuffle(arr) {
  let len = arr.length;
  for (let i = 0; i < len; i++) {
    let randomIdx = Math.floor(Math.random() * len);
    let copyCurrent = { ...arr[i] };
    let copyRandom = { ...arr[randomIdx] };
    arr[i] = copyRandom;
    arr[randomIdx] = copyCurrent;
  }
  return arr;
}
