import React, { useState, useEffect } from 'react';
import './Nav.css';

export default function Nav(props) {
  const [time, setTime] = useState(0); // Timer state in seconds
  const [formattedTime, setFormattedTime] = useState('00:00:00'); // Formatted time for display
  const [isPaused, setIsPaused] = useState(false); // Pause state
  const [intervalId, setIntervalId] = useState(null); // Timer interval ID

  useEffect(() => {
    if (time !== 0 && !isPaused) {
      const id = setInterval(() => {
        setTime(prevTime => prevTime + 1);
      }, 1000);
      setIntervalId(id);
      return () => clearInterval(id);
    } else if (isPaused && intervalId) {
      clearInterval(intervalId); // Clear interval when paused
    }
  }, [time, isPaused, intervalId]);

  useEffect(() => {
    const minutes = Math.floor(time / 60);
    const seconds = time % 60;
    const hours = Math.floor(minutes / 60);
    setFormattedTime(`${String(hours).padStart(2, '0')}:${String(minutes % 60).padStart(2, '0')}:${String(seconds).padStart(2, '0')}`);
  }, [time]);

  const handlePauseResume = () => {
    setIsPaused(!isPaused);
  };

  return (
    <div className="navbar">
      <div className="welcome">
        <h1 className="welcome-text">
          Hi {props.name}! Let's begin.
        </h1>
        <h2 className="timer">{formattedTime}</h2>
        <button onClick={handlePauseResume} className="pause-button">
          {isPaused ? 'Resume' : 'Pause'}
        </button>
      </div>
    </div>
  );
}
