import React, { useState, useEffect } from 'react';
import Card from '../Card/Card';
import Modal from '../Modal/Modal';
import useModal from '../Modal/useModal';
import './Board.css';
import Appa from '../../Assets/Images/Appa.png';
import Katara from '../../Assets/Images/Katara.jpeg';
import PrinceZuko from '../../Assets/Images/Prince_Zuko.jpg';
import Sokka from '../../Assets/Images/Sokka.png';
import Toph from '../../Assets/Images/Toph.jpg';
import Aang from '../../Assets/Images/Aang.jpg';
import backImg from '../../Assets/Images/Logo.jpg';
import Iroh_smiling from '../../Assets/Images/Iroh_smiling.png';
import Mai from '../../Assets/Images/Mai.png';
import momo from '../../Assets/Images/momo.jpg';
import Tylee from '../../Assets/Images/Tylee.png';
import Azulaa from '../../Assets/Images/Azulaa.png';
import Bumi from '../../Assets/Images/Bumi.jpeg';

// Function to build the cards
const buildCards = () => {
  let id = 0;
  const images = { Aang, Appa, Katara, PrinceZuko, Sokka, Toph, Iroh_smiling, Mai, momo, Tylee, Azulaa, Bumi };
  return Object.keys(images)
    .flatMap((type) => [
      { id: id++, type, backImg, frontImg: images[type], flipped: false },
      { id: id++, type, backImg, frontImg: images[type], flipped: false },
    ])
    .sort(() => Math.random() - 0.5);
};

const Board = ({ startTimer, stopTimer }) => {
  const deck = buildCards(); // Call buildCards here after it's defined
  const [cards, setCards] = useState(deck);
  const [checkers, setCheckers] = useState([]);
  const [completed, setCompleted] = useState([]);
  const { isShowing, toggle } = useModal();
  const [isGameCompleted, setIsGameCompleted] = useState(false);

  const onCardClick = (card) => () => {
    if (checkers.length === 0 && !isGameCompleted) {
    }

    if (checkersFull(checkers) || cardAlreadyInCheckers(checkers, card)) return;

    const newCheckers = [...checkers, card];
    setCheckers(newCheckers);

    if (validateCheckers(newCheckers)) {
      setCompleted((prev) => [...prev, newCheckers[0].type]);
    }

    if (checkersFull(newCheckers)) {
      resetCheckersAfter(1000);
    }

    if (completed.length === 12 && !isGameCompleted) {
      setIsGameCompleted(true);
      toggle(); // Show the modal when the game is completed
      stopTimer(); // Stop the timer when the game is completed
    }
  };

  const resetCheckersAfter = (time) => {
    setTimeout(() => {
      setCheckers([]);
    }, time);
  };

  const validateCheckers = (checkers) => {
    return checkers.length === 2 && checkers[0].type === checkers[1].type;
  };

  const cardAlreadyInCheckers = (checkers, card) => {
    return checkers.length === 1 && checkers[0].id === card.id;
  };

  const checkersFull = (checkers) => {
    return checkers.length === 2;
  };

  useEffect(() => {
    setCards((cards) =>
      cards.map((card) => ({
        ...card,
        flipped: checkers.some((c) => c.id === card.id) || completed.includes(card.type),
      }))
    );
  }, [checkers, completed]);

  return (
    <>
      <div className="Board">
        {cards.map((card) => (
          <Card {...card} onClick={onCardClick(card)} key={card.id} />
        ))}
      </div>
      <Modal isShowing={isShowing} hide={toggle} />
    </>
  );
};

export default Board;
