import { useState } from 'react';

const useModal = () => {
  const [isShowing, setIsShowing] = useState(false);

  function toggle() {
    setIsShowing(!isShowing);
  }

  const handleModalClose = () => {
    // Reset the flipped state of all cards when modal closes
    const resetCards = cards.map(card => ({
      ...card,
      flipped: false, // Flip all cards back over
    }));
    setCards(resetCards);
    toggle(); // Close the modal
    setIsGameCompleted(false); // Reset the game completion state
  };

  return {
    isShowing,
    toggle,
  }
};

export default useModal;
