import React, {Component} from 'react'


class HomePage extends Component {
  state = {};

  handleChange = (e) => {
    this.setState({ name: e.target.value });
  };

  handleClick = (e) => {
    e.preventDefault();
    this.props.name(this.state.name, false);

  };

  render() {
    return (
      <div>
        <h1>Welcome to Avatar: The Last Airbender Memory Game.</h1>
        <p> Objective: Click a card to flip it over and try to find it's match. Once you have found all pairs you have won the game. Insert your name below to begin.</p>
        <input type="text" onChange={this.handleChange} placeholder="Name" />
        <button onClick={this.handleClick}>Submit</button>
      </div>
    )
  }
}
export default HomePage;
