import React, { useState, useEffect } from 'react';

const MyStopwatch = ({ setStartFunction, setStopFunction }) => {
  const [time, setTime] = useState(0);
  const [isRunning, setIsRunning] = useState(false);
  let timer;

  useEffect(() => {
    if (isRunning) {
      timer = setInterval(() => {
        setTime((prevTime) => prevTime + 1);
      }, 1000);
    } else {
      clearInterval(timer);
    }
    return () => clearInterval(timer);
  }, [isRunning]);

  useEffect(() => {
    if (setStartFunction) {
      setStartFunction(() => start);
    }
    if (setStopFunction) {
      setStopFunction(() => stop);
    }
  }, [setStartFunction, setStopFunction]);

  const start = () => {
    setIsRunning(true);
  };

  const stop = () => {
    setIsRunning(false);
  };

  return <p>Time: {time}s</p>;
};

export default MyStopwatch;
