import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import './HomePage.css'


export default function HomePage(props) {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    user_image: "",
  })

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    })
}

  const handleSubmit = async (e) => {
    e.preventDefault();
    const userData = await registerUser(formData);
    props.setCurrentUser(userData);
    props.history.push('/trails');
  }


    return (
      <div className="main">
        <h1>Welcome to Avatar: The Last Airbender Memory Game</h1>
        <p> Objective: Click a card to flip it over and try to find its match. Once you have found all pairs you have won the game. Enter your name to begin.</p>

        {/* <Link to="/gamepage"><button>Enter</button></Link> */}
        <form onSubmit={handleSubmit}>
        <input type="text" value={formData.name} onChange={handleChange} />
          <button>Enter</button>
          </form>
      </div>
    )
  }
