import React from 'react';
import { useStopwatch } from 'react-timer-hook';
import './MyStopwatch.css';

export default function MyStopwatch(props) {
  const {
    seconds,
    minutes,
    hours,
    start,
    pause,
    reset,
  } = useStopwatch({ autoStart: false });


  return (
  <div className="div-timer" style={{textAlign: 'center'}}>
      <div>
        <span>{hours}:</span><span>{minutes}:</span><span>{seconds}</span>
      </div>

      <button className="timer-start" onClick={start}><img src="https://res.cloudinary.com/darnycya/image/upload/v1635909257/Play_rrzfp3.png"></img></button>
      <button className="timer-pause" onClick={pause}><img src="https://res.cloudinary.com/darnycya/image/upload/v1635909257/Pause_xopdxv.png"></img></button>
      <button className="timer-reset" onClick={reset}><p className="timer-text">Stop</p></button>

    </div>
  );
}
